		bra.w	loc_8D6
; ---------------------------------------------------------------------------

loc_8B8:				; set trigger
		bset	d1,((VINT_FUNCS_ENABLED_BITFIELD-$1000000)).w
		bra.w	loc_8D6
; ---------------------------------------------------------------------------

loc_8C0:
		clr.w	d0
		clr.w	d1
		moveq	#7,d7

loc_8C6:
		tst.l	(a0)+
		beq.s	loc_8CC
		bset	d1,d0

loc_8CC:
		addq.w	#1,d1
		dbf	d7,loc_8C6
		move.b	d0,((VINT_FUNCS_ENABLED_BITFIELD-$1000000)).w

loc_8D6:
		move	d6,sr
		movem.l	(sp)+,d0-a6
		rte
; End of function Trap9_ManageContextualFunctions


; =============== S U B	R O U T	I N E =======================================


updateSoundAndInputAndPalettes:
		move.w	#$100,(Z80BusReq).l ; D8 ( W)	0: BUSREQ CANCEL
					;	    1: BUSREQ REQUEST
					;    ( R)   0: CPU FUNCTION STOP ACCESSIBLE
					;	    1: FUNCTIONING

loc_8E6:				; D8 ( W)   0: BUSREQ CANCEL
		btst	#0,(Z80BusReq).l ;	     1:	BUSREQ REQUEST
					;    ( R)   0: CPU FUNCTION STOP ACCESSIBLE
					;	    1: FUNCTIONING
		bne.s	loc_8E6		; wait for Z80 bus available
		tst.b	((WAIT_FOR_MUSIC_END-$1000000)).w ; if set, check if music's being played
		beq.w	loc_906
		tst.b	(Z80_CHANNEL_1_NOT_IN_USE).l
		beq.w	loc_9F6
		clr.b	((WAIT_FOR_MUSIC_END-$1000000)).w ; if set, check if music's being played

loc_906:				; up to	4 word values :
		lea	((SOUND_COMMAND_QUEUE-$1000000)).w,a0 ;	xx FC :	update music volume to xx
		move.l	4(a0),d0
		or.l	(a0),d0		; if stack void, do nothing
		beq.w	loc_9F6
		move.b	(a0),d1		; contains music volume	parameter when command is FC
					; or fade in parameter when 7F < command < F0
		move.b	1(a0),d0	; stores first command in d1 and d0 and	pushes the rest	forward
		move.w	2(a0),(a0)+

loc_91E:
		move.w	2(a0),(a0)+
		move.w	2(a0),(a0)+
		clr.w	(a0)
		cmpi.b	#$FB,d0
		bne.s	loc_95A		; if command FB, play back previous music
					; NOTE : for future cube save/resume feature,
					; send proper resume command instead of	previous music index !
					; Cube should save current music at every new music index !
		tst.b	((MUSIC_STACK_LENGTH-$1000000)).w ; up to $A
		beq.s	loc_94E
		movem.l	d7-a0,-(sp)
		lea	((MUSIC_STACK-$1000000)).w,a0 ;	history	of music previously played
		moveq	#8,d7

loc_93E:
		move.b	1(a0),(a0)+
		dbf	d7,loc_93E
		movem.l	(sp)+,d7-a0
		subq.b	#1,((MUSIC_STACK_LENGTH-$1000000)).w ; up to $A

loc_94E:				; history of music previously played
		move.b	((MUSIC_STACK-$1000000)).w,(Z80_SoundDriverCommand).l
		bra.w	loc_9F6
; ---------------------------------------------------------------------------

loc_95A:
		cmpi.b	#$FD,d0
		bcs.s	loc_96A
		move.b	d0,(Z80_SoundDriverCommand).l ;	if command >= FD, then send it to Z80
		bra.w	loc_9F6
; ---------------------------------------------------------------------------

loc_96A:
		cmpi.b	#$F0,d0
		bne.s	loc_97A
		move.b	#1,((WAIT_FOR_MUSIC_END-$1000000)).w ; if F0, then wait	for current music to end before	sending	commands to Z80
		bra.w	loc_9F6
; ---------------------------------------------------------------------------

loc_97A:
		cmpi.b	#$FC,d0
		bne.s	loc_994		; if FC, then update music level
		andi.b	#$F,d1
		move.b	d1,(Z80_SoundDriverMusicLevel).l
		move.b	d0,(Z80_SoundDriverCommand).l
		bra.w	loc_9F6
; ---------------------------------------------------------------------------

loc_994:
		movem.l	d0,-(sp)
		andi.b	#$7F,d0	; ''   ; a music/sfx index mask that must be changed to allow indexes above $80
					; also change stuff at 9AA then	!
		cmp.b	((MUSIC_STACK-$1000000)).w,d0 ;	compare	with last played music
		movem.l	(sp)+,d0
		bne.s	loc_9AA
		bra.w	loc_9F6		; if same to last played music,	let current music play
					; (might play nothing if last played music has ended !)
; ---------------------------------------------------------------------------

loc_9AA:				; if command > 7F, then	send Fade In parameter
		bclr	#7,d0
		beq.s	loc_9B8		; branch if bit	was 1 before bclr
		move.b	d1,(Z80_SoundDriverFadeInData).l
		bra.s	loc_9C6
; ---------------------------------------------------------------------------

loc_9B8:
		cmpi.b	#$40,d0	; '@'
		bgt.s	loc_9C6
		move.b	#$F,(Z80_SoundDriverFadeInData).l ; if music, put this fade in parameter : no fade in

loc_9C6:				; send music/sfx command to Z80
		move.b	d0,(Z80_SoundDriverCommand).l
		cmpi.b	#$40,d0	; '@'
		bge.s	loc_9F6
		movem.l	d7-a0,-(sp)
		lea	((WAIT_FOR_MUSIC_END-$1000000)).w,a0 ; if set, check if	music's being played
		moveq	#8,d7

loc_9DC:				; add music index to music stack
		move.b	-2(a0),-(a0)
		dbf	d7,loc_9DC
		move.b	d0,-(a0)
		movem.l	(sp)+,d7-a0
		cmpi.b	#$A,((MUSIC_STACK_LENGTH-$1000000)).w ;	up to $A
		bge.s	loc_9F6
		addq.b	#1,((MUSIC_STACK_LENGTH-$1000000)).w ; up to $A

loc_9F6:				; converts data	ports 1/2 information into P1/2	inputs State A/B
		bsr.w	UpdatePlayerInputs
		tst.b	((byte_FFDE9D-$1000000)).w
		bne.s	loc_A60
		moveq	#2,d0
		move.b	((P1_INPUT_STATE_A-$1000000)).w,d1 ; STATE A : 80=start, 40=A, 20=C, 10=B, 8=right, 4=left, 2=down, 1=up
					; STATE	B : 80=start, 40=A, 20=C, 10=B,	8=mode,	4=X, 2=Y, 1=Z
		lsr.w	#1,d1
		dbcc	d0,*+4
		lsr.w	#1,d1
		dbcc	d0,*+4
		lsr.w	#1,d1
		dbcc	d0,*+4
		lsr.w	#1,d1
		dbcc	d0,*+4
		tst.w	d0
		bgt.s	loc_A54		; if only one direction	pushed,	set it as primary
		move.b	((PRIMARY_WALKING_DIRECTION-$1000000)).w,d0 ; get current walking direction
		eor.b	d0,((P1_INPUT_STATE_A-$1000000)).w ; get new direction
		moveq	#2,d0
		move.b	((P1_INPUT_STATE_A-$1000000)).w,d1 ; STATE A : 80=start, 40=A, 20=C, 10=B, 8=right, 4=left, 2=down, 1=up
					; STATE	B : 80=start, 40=A, 20=C, 10=B,	8=mode,	4=X, 2=Y, 1=Z
		lsr.w	#1,d1
		dbcc	d0,*+4
		lsr.w	#1,d1
		dbcc	d0,*+4
		lsr.w	#1,d1
		dbcc	d0,*+4
		lsr.w	#1,d1
		dbcc	d0,*+4
		tst.w	d0
		bgt.s	loc_A52
		andi.b	#3,((P1_INPUT_STATE_A-$1000000)).w ; if	both directions	are new, only keep vertical one

loc_A52:
		bra.s	loc_A60
; ---------------------------------------------------------------------------

loc_A54:				; if only one direction	pushed,	set it as primary
		move.b	((P1_INPUT_STATE_A-$1000000)).w,d1
		andi.w	#$F,d1
		move.b	d1,((PRIMARY_WALKING_DIRECTION-$1000000)).w ; first direction moved (same as above) if still moving (or	holding	move buttons)

loc_A60:				; STATE	A : 80=start, 40=A, 20=C, 10=B,	8=right, 4=left, 2=down, 1=up
		move.b	((P1_INPUT_STATE_A-$1000000)).w,d0 ; STATE B : 80=start, 40=A, 20=C, 10=B, 8=mode, 4=X,	2=Y, 1=Z
		move.b	d0,((CURRENT_PLAYER_INPUT-$1000000)).w ; overwrite P2 6-button data with P1 state with walking direction
		cmp.b	((LAST_PLAYER_INPUT-$1000000)).w,d0
		bne.s	loc_A86		; if input is new
		addq.b	#1,((byte_FFDEED-$1000000)).w ;	if input is the	same then increment counter and	ignore input
		cmpi.b	#$18,((byte_FFDEED-$1000000)).w	; YET TO FIGURE	OUT : some kind	of counter related to walking, but also	related	to portrait (wtf ...)
		bcc.s	loc_A80
		clr.b	((CURRENT_PLAYER_INPUT-$1000000)).w ; keep current input only when counter reaches $18
		bra.s	loc_A84
; ---------------------------------------------------------------------------

loc_A80:				; YET TO FIGURE	OUT : some kind	of counter related to walking, but also	related	to portrait (wtf ...)
		subq.b	#6,((byte_FFDEED-$1000000)).w

loc_A84:
		bra.s	loc_AAC
; ---------------------------------------------------------------------------

loc_A86:				; if input is new
		clr.w	d2
		move.b	((LAST_PLAYER_INPUT-$1000000)).w,d1
		andi.b	#$F,d1
		beq.s	loc_A94
		moveq	#1,d2		; was pushing a	direction

loc_A94:				; Contains P1_INPUT_STATE_A when FFDEED	counter	max reached
		move.b	((CURRENT_PLAYER_INPUT-$1000000)).w,((LAST_PLAYER_INPUT-$1000000)).w
		andi.b	#$F,d0
		beq.w	loc_AA8
		tst.b	d2
		bne.w	loc_AAC

loc_AA8:				; YET TO FIGURE	OUT : some kind	of counter related to walking, but also	related	to portrait (wtf ...)
		clr.b	((byte_FFDEED-$1000000)).w

loc_AAC:				; D8 ( W)   0: BUSREQ CANCEL
		move.w	#0,(Z80BusReq).l ;	     1:	BUSREQ REQUEST
					;    ( R)   0: CPU FUNCTION STOP ACCESSIBLE
					;	    1: FUNCTIONING
		bsr.w	sub_19F8	; related to palette updating
		rts
; End of function updateSoundAndInputAndPalettes


