; fade subroutine to comment again ;_;

ParseFadingFX:
		move.b	((FADING_SETTING-$1000000)).w,d0 ; 1 : in from black
					; 2 : out to black
					; 3 : in from white
					; 4 : out to white
		beq.w	return_B1C
		subq.b	#1,((FADING_COUNTER-$1000000)).w ; has to be 01	to start fade routine
		bne.w	return_B1C
		move.b	((FADING_COUNTER_MAX-$1000000)).w,((FADING_COUNTER-$1000000)).w	; has to be 01 to start	fade routine
		subq.b	#1,d0
		ext.w	d0
		lsl.w	#3,d0		; *8 ? so 8 bytes per entry ?
		move.b	((FADING_POINTER-$1000000)).w,d1 ; decremented when value $8x
		ext.w	d1
		add.w	d1,d0
		move.b	FadingData(pc,d0.w),d1 ; 80 : end
					; 8x : go x backward
		cmpi.w	#$80,d1	; '€'
		bne.s	loc_AEC
		clr.b	((FADING_SETTING-$1000000)).w ;	end of palette fx ?

loc_AEC:
		movem.l	d1,-(sp)
		andi.w	#$F0,d1	; 'ð'
		cmpi.w	#$80,d1	; '€'
		movem.l	(sp)+,d1
		bne.s	loc_B0A
		andi.w	#$F,d1		; go x backwards
		sub.b	d1,((FADING_POINTER-$1000000)).w ; decremented when value $8x
		bra.w	ParseFadingFX	; fade subroutine to comment again ;_;
; ---------------------------------------------------------------------------

loc_B0A:
		ext.w	d1
		move.w	d1,d0
		move.b	((FADING_PALETTES-$1000000)).w,d1
		ext.w	d1
		bsr.w	doSomethingWithPalettes1or2
		addq.b	#1,((FADING_POINTER-$1000000)).w ; decremented when value $8x

return_B1C:
		rts
; End of function ParseFadingFX

