		beq.w	return_7EC4
		btst	#0,((P1_INPUT_STATE_A-$1000000)).w ; STATE A : 80=start, 40=A, 20=C, 10=B, 8=right, 4=left, 2=down, 1=up
					; STATE	B : 80=start, 40=A, 20=C, 10=B,	8=mode,	4=X, 2=Y, 1=Z
		beq.s	loc_7E58
		btst	#7,(SAVE_FLAGS).l ; bits 0/1 :busy slots flags
					; bit 7	: game completed
		bne.w	nullsub_17

loc_7E58:
		tst.b	((CONFIGURATION_MODE_ACTIVATED-$1000000)).w
		beq.w	return_7EC4
		trap	#5
; ---------------------------------------------------------------------------
		dc.w $1C0		; Configuration....{D3}
; ---------------------------------------------------------------------------
		trap	#5
; ---------------------------------------------------------------------------
		dc.w $1C2		; {CLEAR}Special Turbo
; ---------------------------------------------------------------------------
		jsr	j_YesNoChoiceBox
		tst.w	d0
		bne.s	loc_7E78
		move.b	#$FF,((SPECIAL_TURBO_CHEAT-$1000000)).w

loc_7E78:
		trap	#5
; ---------------------------------------------------------------------------
		dc.w $1C3		; {CLEAR}Control Opponent
; ---------------------------------------------------------------------------
		jsr	j_YesNoChoiceBox
		tst.w	d0
		bne.s	loc_7E8C
		move.b	#$FF,((CONTROL_OPPONENT_CHEAT-$1000000)).w

loc_7E8C:
		trap	#5
; ---------------------------------------------------------------------------
		dc.w $1C4		; {CLEAR}Auto Battle
; ---------------------------------------------------------------------------
		jsr	j_YesNoChoiceBox
		tst.w	d0
		bne.s	loc_7EA0
		move.b	#$FF,((AUTO_BATTLE_CHEAT-$1000000)).w

loc_7EA0:
		trap	#5
; ---------------------------------------------------------------------------
		dc.w $1C7		; {CLEAR}Game Completed
; ---------------------------------------------------------------------------
		jsr	j_YesNoChoiceBox
		tst.w	d0
		bne.s	loc_7EB8
		bset	#7,(SAVE_FLAGS).l ; bits 0/1 :busy slots flags
					; bit 7	: game completed
		bra.s	loc_7EC0
; ---------------------------------------------------------------------------

loc_7EB8:				; bits 0/1 :busy slots flags
		bclr	#7,(SAVE_FLAGS).l ; bit	7 : game completed

loc_7EC0:
		trap	#5
; ---------------------------------------------------------------------------
		dc.w $1CD		; Configuration	is done.{N}Go ahead!{W1}
; ---------------------------------------------------------------------------

return_7EC4:
		rts
; End of function CheatModeConfiguration


