checkRegion:
		move.b	(HW_Info).l,d0	; 7-MODE  (R)  0: Domestic Model
					;	       1: Overseas Model
					; 6-VMOD  (R)  0: NTSC CPU clock 7.67 MHz
					;	       1: PAL CPU clock	7.60 MHz
					; 5-DISK  (R)  0: FDD unit connected
					;	       1: FDD unit not connected
					; 4-RSV	  (R)  Currently not used
					; 3-0 VER (R)  MEGA DRIVE version ($0 to $F)
		andi.b	#$C0,d0
		cmpi.b	#$80,d0
		beq.w	return_7F40
		bsr.w	enableDisplayAndInterrupts
		lea	aDevelopedForUseOnlyWith(pc), a0 ; "DEVELOPED FOR USE ONLY WITH"
		lea	(byte_FFC286).l,a1
		bsr.w	loadRegionCheckString
		lea	aNtscGenesys(pc), a0 ; "  NTSC GENESYS"
		lea	(byte_FFC350).l,a1
		bsr.w	loadRegionCheckString
		lea	aSystems_(pc), a0 ; "SYSTEMS."
		lea	(byte_FFC41A).l,a1
		bsr.w	loadRegionCheckString
		lea	(byte_FFC000).l,a0
		lea	($C000).l,a1
		move.w	#$400,d0
		moveq	#2,d1
		bsr.w	bwahDMAstuffAgainbis ; d0 = DMA	destination ?
		lea	(PALETTE_1).l,a0
		move.l	#$EEE,(a0)+
		clr.l	(a0)+
		clr.l	(a0)+
		clr.l	(a0)+
		clr.l	(a0)+
		clr.l	(a0)+
		clr.l	(a0)+
		clr.l	(a0)+
		bsr.w	storeVdpCommandster
		bsr.w	setFFDE94b3andWait ; set bit 3 of 0xFFDE94 and wait next Vint to execute sub_740

loc_7F3E:
		bra.s	loc_7F3E
; ---------------------------------------------------------------------------

return_7F40:
		rts
; End of function checkRegion


; =============== S U B	R O U T	I N E =======================================


loadRegionCheckString:
		clr.w	d0
		move.b	(a0)+,d0
		beq.s	return_7F4C
		move.w	d0,(a1)+
		bra.s	loadRegionCheckString
; ---------------------------------------------------------------------------

return_7F4C:
		rts
; End of function loadRegionCheckString

