; <HARDCODED>
; In: D0 = combatant idx
;     D3 = starting item slot
; Out: D1 = item idx
;      D2 = item slot

GetNextUsableAttackItem:
		movem.l	d0/d3-a6,-(sp)
		bsr.w	CheckMuddled2	; In: D0 = combatant entry
					; Out: D1 = whether combatant is inflicted with	MUDDLE 2
		move.w	d1,d6
		btst	#7,d0
		bne.s	loc_D0C0
		move.w	#1,d6

loc_D0C0:
		move.w	d3,d1
		jsr	GetItemAndNumberOfItems	; In: D0 = entity idx
					;     D1 = item	slot
					; Out: D1 = item idx
					;      D2 = number of items held
		cmpi.w	#$7F,d1	; ''
		bne.s	loc_D0D2
		bra.w	loc_D0DC
; ---------------------------------------------------------------------------

loc_D0D2:				; carry	set : YES
		jsr	isItemUsableInBattle
		bcs.w	loc_D0E8

loc_D0DC:
		addq.w	#1,d3
		cmpi.w	#4,d3
		bcs.s	loc_D0C0
		bra.w	loc_D156
; ---------------------------------------------------------------------------

loc_D0E8:
		btst	#7,d1
		bne.w	loc_D0F8
		btst	#$D,d1
		beq.w	loc_D156

loc_D0F8:				; In: D1 = item	idx
		jsr	GetItemDefAddress ; Out: A0 = item address
		move.w	d1,d7
		clr.w	d1
		move.b	9(a0),d1
		tst.b	d6
		beq.s	loc_D13C
		move.w	d1,d5
		andi.b	#$3F,d5	; '?'
		cmpi.b	#$B,d5
		bne.s	loc_D11A
		bra.w	loc_D13C
; ---------------------------------------------------------------------------

loc_D11A:
		cmpi.b	#$C,d5
		bne.s	loc_D124
		bra.w	loc_D13C
; ---------------------------------------------------------------------------

loc_D124:
		cmpi.b	#$D,d5
		bne.s	loc_D12E
		bra.w	loc_D13C
; ---------------------------------------------------------------------------

loc_D12E:
		cmpi.b	#$E,d5
		bne.s	loc_D138
		bra.w	loc_D13C
; ---------------------------------------------------------------------------

loc_D138:
		bra.w	loc_D156
; ---------------------------------------------------------------------------

loc_D13C:				;     Get address of entry in spell definition table.
		jsr	GetSpellDefAddress ;	 In: D1	= spell	index
					;     Out: A0 =	address	of spell #D1 definition
		move.b	3(a0),d2
		andi.b	#3,d2
		bne.w	loc_D156
		move.w	d3,d2
		move.w	d7,d1
		bra.w	loc_D15A
; ---------------------------------------------------------------------------

loc_D156:
		move.w	#$7F,d1	; ''

loc_D15A:
		movem.l	(sp)+,d0/d3-a6
		rts
; End of function GetNextUsableAttackItem


