		lea	(byte_FFC000).l,a1
		move.w	#$700,d7
		jsr	(copyBytes).w	;     Copy D7 bytes from A0 to A1.
					;     In: A0 = src address
					;	  A1 = dest address
					;	  D7 = number of bytes to copy
		lea	titleScreenLayoutA(pc),	a0
		lea	(byte_FFC358).l,a1
		moveq	#$10,d7
		jsr	(copyBytes).w	;     Copy D7 bytes from A0 to A1.
					;     In: A0 = src address
					;	  A1 = dest address
					;	  D7 = number of bytes to copy
		lea	(byte_FFC398).l,a1
		jsr	(copyBytes).w	;     Copy D7 bytes from A0 to A1.
					;     In: A0 = src address
					;	  A1 = dest address
					;	  D7 = number of bytes to copy
		lea	(byte_FFC480).l,a1
		moveq	#$40,d7	; '@'
