					; cleared when fading executed
		clr.b	((FADING_POINTER-$1000000)).w ;	decremented when value $8x
		move.b	((FADING_COUNTER_MAX-$1000000)).w,((FADING_COUNTER-$1000000)).w	; has to be 01 to start	fade routine
		move.b	#1,((FADING_PALETTES-$1000000)).w
		moveq	#$64,d0	; 'd'
		bsr.w	WaitForPlayer1InputStart ; wait	during d0 frames
		moveq	#$20,d0	; ' '
		bsr.w	sub_100218
		move.b	#1,((FADING_SETTING-$1000000)).w ; 1 : in from black
					; 2 : out to black
