; wait during d0 frames

WaitForPlayer1InputStart:
		jsr	(waitNextVint).w ; wait	until VInt func	is called, which clears	RAM:def7
		btst	#7,((P1_INPUT_STATE_A-$1000000)).w ; STATE A : 80=start, 40=A, 20=C, 10=B, 8=right, 4=left, 2=down, 1=up
					; STATE	B : 80=start, 40=A, 20=C, 10=B,	8=mode,	4=X, 2=Y, 1=Z
		bne.w	loc_10029E
		subq.w	#1,d0
		bne.s	WaitForPlayer1InputStart ; wait	during d0 frames
		rts
; End of function WaitForPlayer1InputStart


; =============== S U B	R O U T	I N E =======================================


sub_100218:
		btst	#0,((byte_FFDEA0-$1000000)).w
		bne.s	loc_10022C
		addq.w	#1,(FFD500_MaybeRelatedToVscroll).l
		subq.w	#1,(FFD502_MaybeRelatedToOtherVScrollStuff).l

loc_10022C:
		jsr	(storeVdpCommandsbis).w
		jsr	(setFFDE94b3andWait).w ; set bit 3 of 0xFFDE94 and wait	next Vint to execute sub_740
		btst	#7,((P1_INPUT_STATE_A-$1000000)).w ; STATE A : 80=start, 40=A, 20=C, 10=B, 8=right, 4=left, 2=down, 1=up
					; STATE	B : 80=start, 40=A, 20=C, 10=B,	8=mode,	4=X, 2=Y, 1=Z
		bne.w	loc_10029E
		subq.w	#1,d0
		bne.s	sub_100218
		rts
; End of function sub_100218


